import { Button } from "@mui/material"
import Flex from "./Flex";
import "./Flex.sass";
import React, { useEffect, useState } from "react";

function FlexButton() {
    const languages = ["Python", "Java", "C#", "C++", "C"];
    const [index, setIndex] = useState(0);
    let language = languages[index];
    const handleClick = () => {
        Flex();
        setClicked(true);
    };
    let handleSwipe = (() => {
        console.log("clicked");
        let newInd = (index + 1) % 5;
        setIndex(newInd);
    });

    const [clicked, setClicked] = useState(false);
    let output = (<div class="animate__animated animate__fadeInDown__infinite" style = {{padding: "0", display: "inline"}}>{language}</div>);
    if (!clicked) {
        return (
            <div>
                <br></br><br></br><br></br><br></br>
                <button onClick={handleClick} className="button-45">Languages</button>
            </div>
        )
    }
    else {
        return (
            <div class="flex language ">
                <p>Comfortable with: </p>
                <div class="animate__animated animate__fadeInDown__infinite" style = {{padding: "0", display: "inline"}}>{language}</div>
                <br/><button onClick={handleSwipe} class="material-symbols-outlined" style={{ scale: "1", verticalAlign: "middle", padding: "0px", display: "inline-block"}}>
                    chevron_right
                </button>
            </div>
        )
    }
}

export default FlexButton;
